function statusChangeCallback(n){console.log("statusChangeCallback");console.log(n);n.status==="connected"?loginResponse():document.getElementById("status").innerHTML="Facebook: Not logged in"}function checkLoginState(){FB.getLoginStatus(function(n){statusChangeCallback(n)});location.reload()}function loginResponse(){console.log("Welcome!  Fetching your information.... ");FB.api("/me",function(n){console.log("Successful login for: "+n.name);document.getElementById("status").innerHTML="Facebook: Logged in as: "+n.name;document.getElementById("fb-user").dataset.fbuser=n.name})}FB.getLoginStatus(function(n){statusChangeCallback(n)});