$.fn.isInViewport=function(){var n=$(this).offset().top,i=n+$(this).outerHeight(),t=$(window).scrollTop(),r=t+$(window).height();return i>t&&n<r};let wasInViewPort;$(window).on("resize scroll",function(){const n=$("#favorites-header").isInViewport();n!==wasInViewPort&&$("#favorites-header").css({transform:"translateX(+100%)"});wasInViewPort=n});